     const data = new FormData();
     const token = document.querySelector('meta[name="csrf-token"]').content;
     const pixelId = window.pixelId;
     const eventSourceUrl = window.location.href;
     data.append("authenticity_token", token);
     data.append("pixel_args", JSON.stringify(args));
     data.append("pixel_id", pixelId);
     data.append("event_source_url", eventSourceUrl);
     // if fbclid is null, appending it to data would get serialized as the string `"null"` for
     // rails params, not `nil`
     fbclid && data.append("fbclid", fbclid);

     navigator.sendBeacon("/private_api/analytics/fbpixel", data);
   }

